import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import {
  FaCalendarAlt,
  FaClock,
  FaTrash,
  FaUserShield,
} from "react-icons/fa";
import { BsCheckCircleFill, BsXCircleFill } from "react-icons/bs";

function LeaveRequests() {
  const [requests, setRequests] = useState([]);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch logged-in user
    axios
      .get("http://localhost:4000/api/users/me", {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      })
      .then((res) => {
        setUser(res.data);
        if (!["admin", "employee"].includes(res.data.role)) {
          navigate("/unauthorized");
        }
      })
      .catch((err) => {
        navigate("/login");
      });

    // Fetch leave requests
    axios
      .get("http://localhost:4000/api/leaves", {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      })
      .then((res) => setRequests(res.data))
      .catch((err) => console.error("Error fetching leave data:", err));
  }, [navigate]);

  const handleAction = async (id, action) => {
    await axios.put(
      `http://localhost:4000/api/leaves/${id}/${action}`,
      {},
      {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      }
    );
    setRequests((prev) =>
      prev.map((r) =>
        r._id === id
          ? { ...r, status: action === "approve" ? "Approved" : "Rejected" }
          : r
      )
    );
  };

  const handleDelete = async (id) => {
    await axios.delete(`http://localhost:4000/api/leaves/${id}`, {
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
    });
    setRequests((prev) => prev.filter((r) => r._id !== id));
  };

  if (!user) return null;

  return (
    <div className="container my-5">
      <div className="bg-white rounded-4 p-4 shadow-lg mb-4">
        <h2 className="mb-1">
          <FaCalendarAlt className="me-2" />
          Leave Requests
        </h2>
        <p className="text-muted">View & manage employee leave applications</p>
      </div>

      {requests.map((request) => (
        <div key={request._id} className="bg-white rounded-4 p-4 shadow-sm mb-4">
          <div className="d-flex align-items-center mb-3">
            <img
              src={
                request.user?.profilePhoto
                  ? `http://localhost:4000/uploads/${request.user.profilePhoto}`
                  : "/default-avatar.png"
              }
              onError={(e) => (e.target.src = "/default-avatar.png")}
              alt={request.user?.name}
              width="45"
              height="45"
              className="rounded-circle me-3 border"
              style={{ objectFit: "cover" }}
            />
            <div>
              <h5 className="mb-0">{request.user?.name || "Unknown User"}</h5>
              <small className="text-muted">{request.user?.designation || "Employee"}</small>
            </div>
            <span
              className={`badge ms-auto rounded-pill px-3 py-2 ${
                request.status === "Pending"
                  ? "bg-primary"
                  : request.status === "Approved"
                  ? "bg-success"
                  : "bg-danger"
              }`}
            >
              {request.status}
            </span>
          </div>

          <p>
            <FaCalendarAlt className="me-2 text-muted" />
            <strong>From:</strong> {new Date(request.fromDate).toDateString()}
          </p>
          <p>
            <FaCalendarAlt className="me-2 text-muted" />
            <strong>To:</strong> {new Date(request.toDate).toDateString()}
          </p>
          <p>
            <FaClock className="me-2 text-muted" />
            <strong>Duration:</strong> {request.duration} days
          </p>
          <p>
            <strong>Reason:</strong> {request.reason}
          </p>
          <small className="text-muted">
            Applied on {new Date(request.createdAt).toDateString()}
          </small>

          <div className="mt-3 d-flex gap-2">
            <button
              className="btn btn-success px-3"
              onClick={() => handleAction(request._id, "approve")}
              disabled={request.status !== "Pending"}
            >
              <BsCheckCircleFill className="me-2" />
              Approve
            </button>
            <button
              className="btn btn-danger px-3"
              onClick={() => handleAction(request._id, "reject")}
              disabled={request.status !== "Pending"}
            >
              <BsXCircleFill className="me-2" />
              Reject
            </button>
            <button
              className="btn btn-outline-danger px-3"
              onClick={() => handleDelete(request._id)}
            >
              <FaTrash className="me-2" />
              Delete
            </button>
          </div>
        </div>
      ))}
    </div>
  );
}


export default LeaveRequestPage;
